let ProductItem = function ({product}) {
    let css = {
        display: "flex",
        width: "300px",
        height: "450px"
    }
    
    let cssImage = {
        maxWidth: "10%",
        height: "auto"
    }
    
    return (
        <product-item>
            <img style={cssImage} src={product.image}></img>
            <h1>{product.title}</h1>
            <h3>{product.price}</h3>
        </product-item>
    )
};

export default ProductItem;
