let ProductItem = function ({product}) {
    let css = {
        display: "flex",
        flexWrap: "wrap",
        gap: "200px",
        padding: "50px",
    }
    
    let cssImage = {
        maxWidth: "50%",

        height: "auto"
    }
    return (
        <product-item style={css}>
            <img style={cssImage} src={product.image}></img>
            <h1>{product.title}</h1>
            <h3>{product.price}</h3>
        </product-item>
    )
};

export default ProductItem;
