import { useEffect, useState} from "react";



function App() {
  let [products, setProducts] = useState([]);

  useEffect(function () {
    fetch("https://fakestoreapi.com/products")
    .then((response) => response.json())
    .then((data) => setProducts(data))
  }, [] );




  return (
    <div className="App">
      {true && products.map(function (product) {
        return (
          <h1>{product}</h1>
        )
      })}
    </div>
  );
}

export default App;
