import { useEffect, useState} from "react";
import ProductsList from "./Components/ProductsList/ProductsList";
import ProductItem from "./Components/ProductItem/ProductItem";

function App() {
  let [products, setProducts] = useState([]);

  let test = function (e) {

    if (e.target === e.currentTarget) return;

    if(e.target.tagName === "MOVIE-CARD") return setIdName(e.target.dataset.id);

    if(e.target.parentNode.tagName === "MOVIE-CARD") return setIdName(e.target.parentNode.dataset.id);

    if(e.target.tagName === 'BUTTON') return setIdName("");

    products.forEach(function (product) {
      console.log(product)
    });

  }

  useEffect(function () {
    fetch("https://fakestoreapi.com/products")
    .then((response) => response.json())
    .then((data) => setProducts(data))
  }, [] );

  return (
    <ProductsList onClick={test}>
      {true && products.map(function (product) {
        return (
            <ProductItem product={product} key={product.id}></ProductItem>
        )
      })}
    </ProductsList>
  );
}

export default App;
