let ProductItem = function ({product}) {
    let cssImage = {
        // maxWidth: "25%",
        height: "auto"
    }
    return (
        <product-item>
            <img style={cssImage} src={product.image}></img>
            <h1>{product.title}</h1>
            <h3>{product.price}</h3>
        </product-item>
    )
};

export default ProductItem;
