let ProductItem = function ({product}) {
    let css = {
        display: "flex",
        flexWrap: "wrap",
        backgroundColor: "red",
        flrxDirection: "column"
    }
    
    let cssImage = {
        maxWidth: "10%",
        height: "auto"
    }
    
    return (
        <product-item style={css}>
            <img style={cssImage} src={product.image}></img>
            <div style={css}>
                <h1>{product.title}</h1>
                <h3>{product.price}</h3>    
            </div>
        </product-item>
    )
};

export default ProductItem;
