import { useEffect, useState} from "react";
import ProductsList from "./Components/ProductsList/ProductsList";
import ProductItem from "./Components/ProductItem/ProductItem";

function App() {
  let [products, setProducts] = useState([]);



  let test = function (e) {
    let x = document.querySelector("products-list");
    x.innerHTML = "";
    fetch(`https://fakestoreapi.com/products/${e.currentTarget.id}`)
    .then(res=>res.json())
    .then(json=>console.log(json))
  }

  useEffect(function () {
    fetch("https://fakestoreapi.com/products")
    .then((response) => response.json())
    .then((data) => setProducts(data))
  }, [] );

  return (
    <ProductsList>
      {true && products.map(function (product) {
        return (
            <ProductItem product={product} key={product.id} onClick={test}></ProductItem>
        )
      })}
    </ProductsList>
  );
}

export default App;
