import ProdcutItem from "./ProductsList.css"

let ProductItem = function ({product}) {
    let css = {
        display: "grid",
        gridTemplateColumns: "30% auto auto",
    }
    
    let cssImage = {
        height: "auto",
        maxWidth: "170px",
        placeSelf: "center"
    }
    
    return (
        <product-item style={css}>
            <img  style={cssImage} src={product.image}></img>
            
                <h1>{product.title}</h1>
                <h3>{product.price}</h3>    
            
               
        </product-item>
    )
};

export default ProductItem;
