import { useEffect, useState} from "react";
import ProductsList from "./Components/ProductsList/ProductsList";
import ProductItem from "./Components/ProductItem/ProductItem";
import ProductDetails from "./Components/ProductDetails/ProductDetails";
import { Route, Routes } from "react-router-dom";

function App() {
  let [products, setProducts] = useState([]);
  let [productID, setProductID] = useState(null);
  let [productDetails, setProductDetails] = useState({});



  let eventProductID = function (e) {
    setProductID(e.currentTarget.id)
  }


  useEffect(function () {
    fetch("https://fakestoreapi.com/products")
    .then((response) => response.json())
    .then((data) => setProducts(data))
  }, [] );

  useEffect(function () {
    if(productID === null) return;
    fetch(`https://fakestoreapi.com/products/${productID}`)
    .then(function(response) {
      return response.json()
    })
    .then(function (data) {
      setProductDetails(data);
    })
  }, [productID])

  return (
    <Routes>
      <Route path="/" element={<ProductsList>
      {true && products.map(function (product) {
        return (
            <ProductItem product={product} key={product.id} onClick={eventProductID}></ProductItem>
        )
      })}
    </ProductsList>}/>

      <Route path="/details" element={<ProductDetails product={productDetails}></ProductDetails>}></Route>
    </Routes>

  );
}

export default App;
