import "./ProductItem.css";
import StarIcon from "../StarIcon/StarIcon";
import { Link } from "react-router-dom";

let ProductItem = function ({product}) {

  let x = product.price.toString().split(".");
  let price;

  if (x.length === 2) {
    if (x[1].length === 0) price =`${x[0]}.00`;
  
    if (x[1].length === 1) price = `${x[0]}.${x[1]}0`;

    if (x[1].length === 2) price = `${x[0]}.${x[1]}`;
  }

  if(x.length === 1) price = `${x[0]}.00`;


  return (
    <Link to={`/details/${product.id}`}>
      <product-item class="product_item" id={product.id}>
        <product-image>
          <img src={product.image}></img>
        </product-image>
        <product-title>{product.title}</product-title>
        <product-price>${price}</product-price>    
        <product-rating>
          {Object.keys(product).length !== 0 && <StarIcon product={product}></StarIcon>}
        </product-rating>
      </product-item>
    </Link>
  )
};

export default ProductItem;
