import "./ProductDetails.css"
import StarIcon from "../StarIcon/StarIcon"
import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

let ProductDetails = function () {

    let { id } = useParams();
    let [product, setProduct] = useState({});
    let [price, setPrice] = useState("");
    

    useEffect(function () {
        fetch(`https://fakestoreapi.com/products/${id}`)
    .then(res=>res.json())
    .then(json=>setProduct(json))
    }, [id]);

    useEffect(function () {
        if(Object.keys(product).length == 0)  return;
        
        let x = product.price.toString().split(".");

        if (x.length === 2) {
            if (x[1].length === 0) setPrice(`${x[0]}.00`);
  
            if (x[1].length === 1) setPrice(`${x[0]}.${x[1]}0`);

            if (x[1].length === 2) setPrice(`${x[0]}.${x[1]}`);
        }

        if(x.length === 1) setPrice(`${x[0]}.00`);
    }, [product])
   
    
 

    return (
        <product-item class="details" key={product.id}>
            <product-image><img src={product.image}></img></product-image>
            <product-title>{product.title}</product-title>
            <product-description>{product.description}</product-description>
            <product-price>${product.price}</product-price>    
            <product-rating>
                {Object.keys(product).length !== 0 && <StarIcon product={product}></StarIcon>} 
            </product-rating>
        
                <button>Add to Cart</button>
        
        </product-item>
    )
}

export default ProductDetails;
