import "./ProductDetails.css"
import StarIcon from "../StarIcon/StarIcon"
import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";



let ProductDetails = function () {
    let { id } = useParams();
    let [product, setProduct] = useState({});
    let [rating, setRating] = useState(null);
    

    useEffect(function () {
        fetch(`https://fakestoreapi.com/products/${id}`)
    .then(res=>res.json())
    .then(json=>setProduct(json))
    }, [id]);
   
    useEffect(function () {
        if(Object.keys(product).length === 0) return;
        setRating([0,0,0,0,0].fill(1,0,product.rating.rate));
    }, [product])

    return (
        <product-item class="details" key={product.id}>
            <product-image><img src={product.image}></img></product-image>
            <product-title>{product.title}</product-title>
            <product-description>{product.description}</product-description>
            <product-price>${product.price}</product-price>    
            <product-rating>
                {Object.keys(product).length !== 0 && rating.Map(function(x) {
                    return (
                        <StarIcon rate={x}></StarIcon>
                    )
                })} 
            </product-rating>
        
                <button>Add to Cart</button>
        
        </product-item>
    )
}

export default ProductDetails;
